import { createBrowserHistory } from 'history'
import * as React from 'react'
import * as ReactDOM from 'react-dom/server'
import * as slugify from 'slug'
import { ACTION_OVERRIDE, createReducer } from '../utils/redux'
import Sandbox, {
  HASH_PREFIX as SANDBOX_HASH_PREFIX,
  SandboxItem,
} from './Sandbox'
import TableOfContents, { TableOfContentsType } from './TableOfContents'

const { useState, useCallback, useEffect, useRef, useMemo } = React

interface ActionSetSendBoxOpenState {
  type: 'SET_SANDBOX_OPEN_STATE'
  payload: boolean
}

const AppReducer = createReducer<AppState, ActionSetSendBoxOpenState>({
  SET_SANDBOX_OPEN_STATE: (state, action: ActionSetSendBoxOpenState) => {
    return {
      ...state,
      sandboxOpen: action.payload,
    }
  },
})

export interface AppContextType {
  registerToSandBox?: (
    title: string,
    children: React.ReactNode,
    sourceCode?: string,
  ) => string
  openSandBox?: (id: string) => void
}
export const AppContext = React.createContext<AppContextType>({})

/* Interface that represents an sandbox example route */
export interface SandboxRoute {
  readonly id: string
  readonly title: string
  readonly children: React.ReactNode
  readonly sourceCode?: string
}

export interface Page {
  id: string
  filepath: string
  url: string
  meta: {
    [key: string]: any
  }
  module: {
    default: React.ComponentType
    tableOfContents: () => TableOfContentsType
  }
  sandboxRoutes: SandboxRoute[]
}

interface AppProps {
  readonly className?: string
  readonly pages: Page[]
}

interface AppState {
  readonly loading: boolean
  readonly showLoadingIndicator: boolean
  readonly currentPage?: Page
  readonly sandboxOpen: boolean
  readonly sandboxItemId?: string
}

export const history = createBrowserHistory()

const reduceSandboxOpenState = (state: AppState) => {
  const { hash } = history.location

  if (hash.startsWith(`#${SANDBOX_HASH_PREFIX}`)) {
    const id = hash.substr(SANDBOX_HASH_PREFIX.length + 1)

    return {
      ...state,
      sandboxOpen: true,
      sandboxItemId: id,
    }
  }

  return state
}

const App: React.SFC<AppProps> = ({ className, pages }) => {
  const categories = useMemo(() => {
    const arr: string[] = []

    pages.forEach(page => {
      if (arr.indexOf(page.meta.category) === -1) {
        arr.push(page.meta.category)
      }

      const sandboxRoutes: SandboxRoute[] = []
      const context: AppContextType = {
        registerToSandBox: (title, children, sourceCode) => {
          const id = slugify(title, { lower: true })
          sandboxRoutes.push({ id, title, children, sourceCode })
          return id
        },
      }

      ReactDOM.renderToString(
        <AppContext.Provider value={context}>
          <page.module.default />
        </AppContext.Provider>,
      )

      page.sandboxRoutes = sandboxRoutes
    })

    return arr
  }, [pages.length])
  const [state, setState] = useState<AppState>(() => ({
    loading: false,
    showLoadingIndicator: false,
    sandboxOpen: false,
    currentPage: pages.find(
      pageItem => history.location.pathname === pageItem.url,
    ),
  }))

  const updatePage = useCallback(async () => {
    const page = pages.find(
      pageItem => history.location.pathname === pageItem.url,
    )

    if (page) {
      setState(oldState => ({
        ...oldState,
        currentPage: page,
      }))
    }
  }, [])

  const onLinkClick = useCallback(
    (event: React.MouseEvent<HTMLAnchorElement>) => {
      // @ts-ignore
      if (event.metaKey) {
        return
      }

      event.preventDefault()
      const anchor = event.target as HTMLAnchorElement
      const path = `${anchor.pathname || '/'}${anchor.hash}`

      history.push(path)
    },
    [true],
  )

  useEffect(() => {
    const updateSandboxState = () => setState(reduceSandboxOpenState)

    // Listen for changes to the current location.
    const unlisten = history.listen(() => {
      updateSandboxState()
      updatePage()
    })

    updateSandboxState()
    return unlisten
  }, [true])

  const contextValue = useMemo<AppContextType>(
    () => ({
      openSandBox: id => {
        history.push(`${history.location.pathname}#${SANDBOX_HASH_PREFIX}${id}`)

        setState(oldState => ({
          ...oldState,
          sandboxOpen: true,
        }))
      },
      registerToSandBox: title => {
        return slugify(title, { lower: true })
      },
    }),
    [setState],
  )
  const handleSandBoxOpenStateChange = (newSandBoxOpenState: boolean) => {
    setState(oldState => ({
      ...oldState,
      sandboxOpen: newSandBoxOpenState,
    }))

    if (!newSandBoxOpenState) {
      history.push(history.location.pathname)
    }
  }

  const { currentPage, loading, showLoadingIndicator } = state
  const tableOfContents = useMemo(
    () =>
      currentPage && currentPage.module.tableOfContents
        ? currentPage.module.tableOfContents()
        : [],
    [currentPage],
  )

  return (
    <div className={className}>
      <div className="Doky__Nav">
        <a href="/" className="Doky__NavItem" onClick={onLinkClick}>
          <img
            src="data:image/png;base64,"
            alt=""
            className="Doky__Brand"
          />
        </a>
        <a href="/" className="Doky__NavItem">
          <svg
            width="24"
            height="24"
            viewBox="0 0 24 24"
            focusable="false"
            role="presentation"
          >
            <path
              d="M16.436 15.085l3.94 4.01a1 1 0 0 1-1.425 1.402l-3.938-4.006a7.5 7.5 0 1 1 1.423-1.406zM10.5 16a5.5 5.5 0 1 0 0-11 5.5 5.5 0 0 0 0 11z"
              fillRule="evenodd"
            />
          </svg>
        </a>
      </div>
      <div className="Doky__Sidebar">
        {categories.map(category => (
          <div key={category || ''} className="Doky__SidebarNavGroup">
            <h3 className="Doky__SidebarNavTitle">{category || 'Others'}</h3>
            {pages.map(
              page =>
                page.meta.category === category && (
                  <React.Fragment key={page.id}>
                    <a
                      key={page.id}
                      href={page.url}
                      onClick={onLinkClick}
                      className={`Doky__SidebarNavItem${
                        currentPage && page.id === currentPage.id
                          ? ' Doky__SidebarNavItem--active'
                          : ''
                      }`}
                    >
                      {page.meta.title}
                      {page.sandboxRoutes.length && (
                        <span className="DokySidebarNavItem__Badge">
                          <svg
                            xmlns="http://www.w3.org/2000/svg"
                            width="16"
                            height="16"
                            viewBox="0 0 24 24"
                            fill="none"
                            stroke="currentColor"
                            strokeWidth="2"
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            className="feather feather-package"
                          >
                            <path d="M12.89 1.45l8 4A2 2 0 0 1 22 7.24v9.53a2 2 0 0 1-1.11 1.79l-8 4a2 2 0 0 1-1.79 0l-8-4a2 2 0 0 1-1.1-1.8V7.24a2 2 0 0 1 1.11-1.79l8-4a2 2 0 0 1 1.78 0z" />
                            <polyline points="2.32 6.16 12 11 21.68 6.16" />
                            <line x1="12" y1="22.76" x2="12" y2="11" />
                            <line x1="7" y1="3.5" x2="17" y2="8.5" />
                          </svg>
                        </span>
                      )}
                    </a>
                    {currentPage &&
                      page.id === currentPage.id &&
                      page.sandboxRoutes.map(sandboxRoute => (
                        <a
                          key={sandboxRoute.id}
                          href={`${page.url}#${SANDBOX_HASH_PREFIX}${
                            sandboxRoute.id
                          }`}
                          onClick={onLinkClick}
                          className={`Doky__SidebarNavSandboxItem`}
                        >
                          {sandboxRoute.title}
                        </a>
                      ))}
                  </React.Fragment>
                ),
            )}
          </div>
        ))}
      </div>
      <Sandbox
        pages={pages}
        open={state.sandboxOpen}
        activeRouteId={state.sandboxItemId}
        onOpenStateChange={handleSandBoxOpenStateChange}
        currentPage={state.currentPage}
      />
      <div className="Doky__Body">
        <div className="Doky__Container">
          <div className="DokyContainerSidebar">
            <div className="DokyContainerSidebar__inner">
              <a
                href={`#${SANDBOX_HASH_PREFIX}`}
                className="DokyContainerSidebar__button btn btn-primary btn--block mb-4 ta-left"
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="18"
                  height="18"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  className="feather feather-package"
                >
                  <path d="M12.89 1.45l8 4A2 2 0 0 1 22 7.24v9.53a2 2 0 0 1-1.11 1.79l-8 4a2 2 0 0 1-1.79 0l-8-4a2 2 0 0 1-1.1-1.8V7.24a2 2 0 0 1 1.11-1.79l8-4a2 2 0 0 1 1.78 0z" />
                  <polyline points="2.32 6.16 12 11 21.68 6.16" />
                  <line x1="12" y1="22.76" x2="12" y2="11" />
                  <line x1="7" y1="3.5" x2="17" y2="8.5" />
                </svg>
                Open Sandbox
              </a>
              {tableOfContents.length > 0 && (
                <TableOfContents value={tableOfContents} />
              )}
            </div>
          </div>
          {!loading && currentPage && !showLoadingIndicator && (
            <>
              <div className="DokyHeader">
                <h1 className="DokyHeader__title">{currentPage.meta.title}</h1>
                <p className="DokyHeader__description">
                  {currentPage.meta.description}
                </p>
                <span className="DokyHeader__legend">Feb 14 . 11 min read</span>
              </div>
              {currentPage.module.default && (
                <AppContext.Provider value={contextValue}>
                  <currentPage.module.default />
                </AppContext.Provider>
              )}
            </>
          )}
          {!loading && !currentPage && history.location.pathname === '/' && (
            <>
              <h1>Pagina inicial</h1>
            </>
          )}

          {showLoadingIndicator && (
            <>
              <span className="Docz__placeholder Docz__placeholderTitle" />
              <span className="Docz__placeholder Docz__placeholderSubtitle mb-4" />
              <span className="Docz__placeholder Docz__placeholderText" />
              <span className="Docz__placeholder Docz__placeholderText" />
              <span className="Docz__placeholder Docz__placeholderText" />
              <span className="Docz__placeholder Docz__placeholderText" />
              <span className="Docz__placeholder Docz__placeholderText w-50 mb-4" />
              <span className="Docz__placeholder Docz__placeholderCode mb-4" />
              <span className="Docz__placeholder Docz__placeholderText" />
              <span className="Docz__placeholder Docz__placeholderText" />
              <span className="Docz__placeholder Docz__placeholderText" />
              <span className="Docz__placeholder Docz__placeholderText" />
              <span className="Docz__placeholder Docz__placeholderText w-50 mb-4" />
            </>
          )}
        </div>
      </div>
    </div>
  )
}

App.displayName = 'App'

export default App
